import React, {useState} from "react";
import styled from "styled-components";
import Tasks from "./Tasks";
const Сontent=(props)=>{
    const Main=styled.main`
        width: 60%;
        margin-left: auto;
        margin-right: auto;
        margin-top: 20px;
      
    `
    const Input=styled.input`
          padding: 15px;
          box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);
          width: 80%;
    `
     let [new_input_value,change_value]=useState("add Task")
     let set_new_val=()=>{
        change_value(e.currentTarget.value)
    }
    console.log(new_input_value)
    return(
        <Main>
            <Input placeholder="Add Task...."  onChange={set_new_val} val={new_input_value}></Input>
            <Tasks name={new_input_value}></Tasks>
        </Main>
    )
}

export default Сontent
